mapper.select:
select
  id, update_time, create_time, update_by, create_by, remark, extra, delete_flag, role_key, role_name, status, num
from sys_role


        and id = #{id}


        and update_time = #{updateTime}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and create_by = #{createBy}


        and remark = #{remark}


        and extra = #{extra}


        and delete_flag = #{deleteFlag}


        and role_key = #{roleKey}


        and role_name = #{roleName}


        and status = #{status}


        and num = #{num}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from sys_role


        and id = #{id}


        and update_time = #{updateTime}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and create_by = #{createBy}


        and remark = #{remark}


        and extra = #{extra}


        and delete_flag = #{deleteFlag}


        and role_key = #{roleKey}


        and role_name = #{roleName}


        and status = #{status}


        and num = #{num}

SELECT sr.id, sr.update_time, sr.create_time, sr.update_by, sr.create_by, sr.remark, sr.extra, sr.delete_flag, sr.role_key, sr.role_name, sr.status, sr.num FROM sys_user su LEFT JOIN sys_user_role sur ON su.id = sur.user_id LEFT JOIN sys_role sr ON sr.id = sur.role_id
AND su.delete_flag = 0
su.id = #{userId}
